// Parse and validate input data
const inputData = items[0].json;

// Extract core elements
const images = inputData.images || [];
const quiz = inputData.quiz || {};
const userId = inputData.userId || 'anonymous_' + Date.now();

// Validate image count
if (images.length < 2 || images.length > 10) {
  throw new Error('Please provide between 2-10 images');
}

// Structure the data for downstream processing
return [
  {
    json: {
      userId,
      timestamp: new Date().toISOString(),
      images,
      tone: quiz.tone || 'authentic',
      vibes: quiz.vibes || [],
      metaphor: quiz.metaphor || 'a cozy coffee shop',
      audience: quiz.audience || 'creative entrepreneurs',
      sampleCopy: quiz.sampleCopy || '',
      imageCount: images.length
    }
  }
];

