// Compile all generated content into structured output
const brandVoice = $node['brand-voice-builder'].json;
const reels = $node['reel-generator'].json;
const carousels = $node['carousel-generator'].json;
const hooks = $node['hook-generator'].json;
const originalInput = $node['input-parser'].json;

// Safety check function
function contentSafetyCheck(content) {
  const bannedWords = ['hate', 'violence', 'explicit', 'drug'];
  const contentString = JSON.stringify(content).toLowerCase();
  return !bannedWords.some(word => contentString.includes(word));
}

// Perform safety check
if (!contentSafetyCheck([brandVoice, reels, carousels, hooks])) {
  throw new Error('Content safety check failed. Please try again.');
}

// Structure the complete output
const output = {
  userId: originalInput.userId,
  timestamp: originalInput.timestamp,
  brandProfile: {
    essence: brandVoice.brand_essence,
    voiceAttributes: brandVoice.voice_attributes,
    contentPillars: brandVoice.content_pillars,
    uniqueAngle: brandVoice.unique_angle,
    emotionalSignature: brandVoice.emotional_signature
  },
  contentBundle: {
    shortForm: reels.videos || reels,
    carousels: carousels.carousels || carousels,
    hooks: hooks.hooks || hooks
  },
  metadata: {
    imageCount: originalInput.imageCount,
    tone: originalInput.tone,
    vibes: originalInput.vibes,
    generatedAt: new Date().toISOString()
  }
};

return [{ json: output }];

// Prepare final response
const savedData = $node['firebase-writer'].json;
const alchemistMessage = $node['alchemist-message'].json.output || $node['alchemist-message'].json;

// Add posting time suggestions based on brand voice
const postingGuidance = {
  bestTimes: determineOptimalPostingTimes($json.brandProfile.voiceAttributes),
  frequencyAdvice: '3-4 times per week to maintain momentum without burnout',
  platformPriority: suggestPlatformOrder($json.brandProfile.contentPillars)
};

function determineOptimalPostingTimes(attributes) {
  if (attributes.includes('professional') || attributes.includes('educational')) {
    return ['Weekdays 8-10am', 'Lunch 12-1pm', 'Evening 5-7pm'];
  } else if (attributes.includes('creative') || attributes.includes('artistic')) {
    return ['Early morning 6-8am', 'Late afternoon 4-6pm', 'Night owls 9-11pm'];
  } else {
    return ['Morning 9-11am', 'Afternoon 2-4pm', 'Evening 7-9pm'];
  }
}

function suggestPlatformOrder(pillars) {
  const platformMap = {
    'education': ['LinkedIn', 'Instagram'],
    'inspiration': ['Instagram', 'TikTok'],
    'community': ['Facebook', 'Instagram'],
    'entertainment': ['TikTok', 'Instagram Reels']
  };

  // Match pillars to platforms
  const platforms = new Set();
  pillars.forEach(pillar => {
    const keywords = pillar.toLowerCase().split(' ');
    Object.keys(platformMap).forEach(key => {
      if (keywords.some(word => word.includes(key))) {
        platformMap[key].forEach(platform => platforms.add(platform));
      }
    });
  });

  return Array.from(platforms).slice(0, 2) || ['Instagram', 'TikTok'];
}

// Final response structure
const response = {
  success: true,
  message: 'Your brand alchemy is complete! âœ¨',
  alchemistMessage,
  data: {
    ...$json,
    postingGuidance,
    firebaseRef: savedData.name || savedData.id
  }
};

return [{ json: response }];
